sect1.screen:
#!/bin/bash echo "SUSE Manager Server Client bootstrap script v4.0" echo echo echo "MINOR MANUAL EDITING OF THIS FILE MAY BE REQUIRED!" echo echo "If this bootstrap script was created during the initial installation" echo "of a SUSE Manager Server, the ACTIVATION_KEYS, and ORG_GPG_KEY values will" echo "probably *not* be set (see below). If this is the case, please do the" echo "following:" echo " - copy this file to a name specific to its use." echo " (e.g., to bootstrap-SOME_NAME.sh - like bootstrap-web-servers.sh.)" echo " - on the website create an activation key or keys for the system(s) to" echo " be registered." echo " - edit the values of the VARIABLES below (in this script) as" echo " appropriate:" echo " - ACTIVATION_KEYS needs to reflect the activation key(s) value(s)" echo " from the website. XKEY or XKEY,YKEY" echo " - ORG_GPG_KEY needs to be set to the name(s) of the corporate public" echo " GPG key filename(s) (residing in /srv/www/htdocs/pub) if appropriate. XKEY or XKEY,YKEY" echo echo "Verify that the script variable settings are correct:" echo " - CLIENT_OVERRIDES should be only set differently if a customized" echo " client-config-overrides-VER.txt file was created with a different" echo " name." echo " - ensure the value of HOSTNAME is correct." echo " - ensure the value of ORG_CA_CERT is correct." echo echo "Enable this script: comment (with #'s) this block (or, at least just" echo "the exit below)" echo ACTIVATION_KEYS=1-dev-sle12-sp2-sap-pool-ppc64le ORG_GPG_KEY= CLIENT_OVERRIDES=client-config-overrides.txt HOSTNAME=[SUMA FQDN] ORG_CA_CERT=RHN-ORG-TRUSTED-SSL-CERT ORG_CA_CERT_IS_RPM_YN=0 USING_SSL=1 USING_GPG=1 REGISTER_THIS_BOX=1 ALLOW_CONFIG_ACTIONS=1 ALLOW_REMOTE_COMMANDS=1 FULLY_UPDATE_THIS_BOX=0 PROFILENAME="" # Empty by default to let it be set automatically. DISABLE_LOCAL_REPOS=1 DISABLE_YAST_AUTOMATIC_ONLINE_UPDATE=1 Z_CLIENT_REPOS_ROOT= MYNAME=`hostname -f` LCMYNAME=`echo $MYNAME | tr '[:upper:]' '[:lower:]'` LCHOSTNAME=`echo $HOSTNAME | tr '[:upper:]' '[:lower:]'`
if [ $LCMYNAME == $LCHOSTNAME ]; then echo "Name of client and of SUSE Manager server are the same." echo "Do not try to register a SUSE Manager server at itself!" echo "Aborting." exit 1 fi if [ -f "/var/adm/autoinstall/cache/SUSE_Manager_keys" ]; then . /var/adm/autoinstall/cache/SUSE_Manager_keys if [ "x$redhat_management_key" != "x" ]; then ACTIVATION_KEYS="$ACTIVATION_KEYS,$redhat_management_key" fi
if [ "x$registration_key" != "x" ]; then ACTIVATION_KEYS="$ACTIVATION_KEYS,$registration_key" fi
ACTIVATION_KEYS=${ACTIVATION_KEYS#,} fi if [ -x /usr/bin/wget ] ; then output=`LANG=en_US /usr/bin/wget --no-check-certificate 2>&1` error=`echo $output | grep "unrecognized option"` if [ -z "$error" ] ; then FETCH="/usr/bin/wget -nv -r -nd --no-check-certificate" else FETCH="/usr/bin/wget -nv -r -nd" fi elif [ -x /usr/bin/curl ] ; then output=`LANG=en_US /usr/bin/curl -k 2>&1` error=`echo $output | grep "is unknown"` if [ -z "$error" ] ; then FETCH="/usr/bin/curl -ksSOf" else FETCH="/usr/bin/curl -sSOf" fi else echo "To be able to download files, please install either 'wget' or 'curl'" exit 1 fi
HTTP_PUB_DIRECTORY=http://${HOSTNAME}/pub HTTPS_PUB_DIRECTORY=https://${HOSTNAME}/pub if [ $USING_SSL -eq 0 ] ; then HTTPS_PUB_DIRECTORY=${HTTP_PUB_DIRECTORY} fi
INSTALLER=up2date if [ -x /usr/bin/zypper ] ; then INSTALLER=zypper elif [ -x /usr/bin/yum ] ; then INSTALLER=yum fi
if [ ! -w . ] ; then echo "" echo "*** ERROR: $(pwd):" echo " No permission to write to the current directory." echo " Please execute this script in a directory where downloaded files can be stored." echo "" exit 1 fi echo echo "UPDATING RHN_REGISTER/UP2DATE CONFIGURATION FILES" echo "-------------------------------------------------" echo "* downloading necessary files" echo " client_config_update.py..." rm -f client_config_update.py $FETCH ${HTTPS_PUB_DIRECTORY}/bootstrap/client_config_update.py echo " ${CLIENT_OVERRIDES}..." rm -f ${CLIENT_OVERRIDES} $FETCH ${HTTPS_PUB_DIRECTORY}/bootstrap/${CLIENT_OVERRIDES}
if [ ! -f "client_config_update.py" ] ; then echo "ERROR: client_config_update.py was not downloaded" exit 1 fi if [ ! -f "${CLIENT_OVERRIDES}" ] ; then echo "ERROR: ${CLIENT_OVERRIDES} was not downloaded" exit 1 fi
echo echo "PREPARE GPG KEYS AND CORPORATE PUBLIC CA CERT" echo "-------------------------------------------------" if [ ! -z "$ORG_GPG_KEY" ] ; then echo echo "* importing organizational GPG keys" for GPG_KEY in $(echo "$ORG_GPG_KEY" | tr "," " "); do rm -f ${GPG_KEY} $FETCH ${HTTPS_PUB_DIRECTORY}/${GPG_KEY} res=$(LC_ALL=C rpm -q --queryformat '%{version}' up2date | sed -e 's/\..*//g') if [ "x$res" == "x2" ] ; then gpg $(up2date --gpg-flags) --import $GPG_KEY else rpm --import $GPG_KEY fi done else echo "* no organizational GPG keys to import" fi
echo if [ $USING_SSL -eq 1 ] ; then echo "* attempting to install corporate public CA cert" test -d /usr/share/rhn || mkdir -p /usr/share/rhn rm -f ${ORG_CA_CERT} $FETCH ${HTTP_PUB_DIRECTORY}/${ORG_CA_CERT}
if [ $ORG_CA_CERT_IS_RPM_YN -eq 1 ] ; then rpm -Uvh --force --replacefiles --replacepkgs ${ORG_CA_CERT} rm -f ${ORG_CA_CERT} else mv ${ORG_CA_CERT} /usr/share/rhn/ fi
if [ "$INSTALLER" == zypper ] ; then function suseVersion() { rpm --eval "%{suse_version}" }
function sslCertDir() { if [[ $(suseVersion) -ge 1315 ]]; then echo "/etc/pki/trust/anchors" else echo "/etc/ssl/certs" fi }
function targetCertPath() { echo "$(sslCertDir)/${ORG_CA_CERT}.pem" }
function updateCertificates() { if [[ $(suseVersion) -ge 1315 ]]; then test -x /usr/sbin/update-ca-certificates && /usr/sbin/update-ca-certificates else test -x /usr/bin/c_rehash && /usr/bin/c_rehash /etc/ssl/certs/ | grep "${ORG_CA_CERT}" fi }
function symlinkCertificate() { if [ $ORG_CA_CERT_IS_RPM_YN -eq 1 ] ; then ORG_CA_CERT=$(basename $(sed -n 's/^sslCACert *= *//p' "${CLIENT_OVERRIDES}")) fi test -e "$(targetCertPath)" || { test -d $(sslCertDir) || mkdir -p $(sslCertDir) ln -s "/usr/share/rhn/${ORG_CA_CERT}" "$(targetCertPath)" } }
symlinkCertificate updateCertificates fi else echo "* configured not to use SSL: don't install corporate public CA cert" fi
if [ "$INSTALLER" == zypper ]; then echo echo "CHECKING THE REGISTRATION STACK" echo "-------------------------------------------------"
function getZ_CLIENT_CODE_BASE() { local BASE="" local VERSION="" local PATCHLEVEL="" test -r /etc/SuSE-release && { grep -q 'Enterprise' /etc/SuSE-release && BASE="sle" eval $(grep '^\(VERSION\|PATCHLEVEL\)' /etc/SuSE-release | tr -d '[:blank:]') } Z_CLIENT_CODE_BASE="${BASE:-unknown}" Z_CLIENT_CODE_VERSION="${VERSION:-unknown}" Z_CLIENT_CODE_PATCHLEVEL="${PATCHLEVEL:-0}" }
function getZ_MISSING() { local NEEDED="spacewalk-check spacewalk-client-setup spacewalk-client-tools zypp-plugin-spacewalk" if [ "$Z_CLIENT_CODE_BASE" == "sle" -a "$Z_CLIENT_CODE_VERSION" == "10" ]; then which 'xsltproc' || NEEDED="$NEEDED libxslt" fi Z_MISSING="" for P in $NEEDED; do rpm -q "$P" || Z_MISSING="$Z_MISSING $P" done }
function getZ_ZMD_TODEL() { local ZMD_STACK="zmd rug libzypp-zmd-backend yast2-registration zen-updater zmd-inventory suseRegister-jeos" if rpm -q suseRegister --qf '%{VERSION}' | grep -q '^\(0\.\|1\.[0-3]\)\(\..*\)\?$'; then # we need the new suseRegister >= 1.4, so wipe an old one too ZMD_STACK="$ZMD_STACK suseRegister suseRegisterInfo spacewalk-client-tools" fi Z_ZMD_TODEL="" for P in $ZMD_STACK; do rpm -q "$P" && Z_ZMD_TODEL="$Z_ZMD_TODEL $P" done }
echo "* check for necessary packages being installed..." getZ_CLIENT_CODE_BASE echo "* client codebase is ${Z_CLIENT_CODE_BASE}-${Z_CLIENT_CODE_VERSION}-sp${Z_CLIENT_CODE_PATCHLEVEL}"
getZ_MISSING if [ -z "$Z_MISSING" ]; then echo " no packages missing." else echo "* going to install missing packages..." Z_CLIENT_REPOS_ROOT="${Z_CLIENT_REPOS_ROOT:-https://${HOSTNAME}/pub/repositories}" Z_CLIENT_REPO_URL="${Z_CLIENT_REPOS_ROOT}/${Z_CLIENT_CODE_BASE}/${Z_CLIENT_CODE_VERSION}/${Z_CLIENT_CODE_PATCHLEVEL}/bootstrap" test "${Z_CLIENT_CODE_BASE}/${Z_CLIENT_CODE_VERSION}/${Z_CLIENT_CODE_PATCHLEVEL}" = "sle/11/1" && { Z_CLIENT_REPO_URL="${Z_CLIENT_REPOS_ROOT}/susemanager-client-setup" } Z_CLIENT_REPO_NAME="susemanager-client-setup" Z_CLIENT_REPO_FILE="/etc/zypp/repos.d/$Z_CLIENT_REPO_NAME.repo"
$FETCH $Z_CLIENT_REPO_URL/repodata/repomd.xml if [ ! -f "repomd.xml" ] ; then echo "Bootstrap repo '$Z_CLIENT_REPO_URL' does not exist." Z_CLIENT_REPO_URL="" fi rm -f repomd.xml if [ "$Z_CLIENT_CODE_BASE" == "sle" ]; then if [ "$Z_CLIENT_CODE_VERSION" = "10" ]; then echo "* check whether to remove the ZMD stack first..." getZ_ZMD_TODEL if [ -z "$Z_ZMD_TODEL" ]; then echo " ZMD stack is not installed. No need to remove it." else echo " Disable and remove the ZMD stack..." if [ -x /usr/sbin/rczmd ]; then /usr/sbin/rczmd stop fi rpm -e --nodeps $Z_ZMD_TODEL || { echo "ERROR: Failed remove the ZMD stack." exit 1 } fi fi fi
if rpm -q zypper --qf '%{VERSION}' | grep -q '^0\(\..*\)\?$'; then
if [ -n "$Z_CLIENT_REPO_URL" ]; then echo " adding client software repository at $Z_CLIENT_REPO_URL" zypper --non-interactive --no-gpg-checks sd $Z_CLIENT_REPO_NAME zypper --non-interactive --no-gpg-checks sa $Z_CLIENT_REPO_URL $Z_CLIENT_REPO_NAME zypper --non-interactive --no-gpg-checks refresh "$Z_CLIENT_REPO_NAME" fi zypper --non-interactive --no-gpg-checks in $Z_MISSING for P in $Z_MISSING; do rpm -q "$P" || { echo "ERROR: Failed to install all missing packages." exit 1 } done if [ -n "$Z_CLIENT_REPO_URL" ]; then cat <<EOF >"$Z_CLIENT_REPO_FILE" [$Z_CLIENT_REPO_NAME] name=$Z_CLIENT_REPO_NAME baseurl=$Z_CLIENT_REPO_URL enabled=1 autorefresh=1 keeppackages=0 gpgcheck=0 EOF fi else
if [ -n "$Z_CLIENT_REPO_URL" ]; then echo " adding client software repository at $Z_CLIENT_REPO_URL" cat <<EOF >"$Z_CLIENT_REPO_FILE" [$Z_CLIENT_REPO_NAME] name=$Z_CLIENT_REPO_NAME baseurl=$Z_CLIENT_REPO_URL enabled=1 autorefresh=1 keeppackages=0 gpgcheck=0 EOF zypper --non-interactive --gpg-auto-import-keys refresh "$Z_CLIENT_REPO_NAME" fi zypper --non-interactive in $Z_MISSING || { echo "ERROR: Failed to install all missing packages." exit 1 }
fi zypper rr "$Z_CLIENT_REPO_NAME" fi if [ "$Z_CLIENT_CODE_BASE" == "sle" ]; then if [ "$Z_CLIENT_CODE_VERSION" = "10" ]; then test -e "/usr/share/zypp/migrate/10-11.migrate.products.sh" && { echo "* check whether we have to to migrate metadata..." sh /usr/share/zypp/migrate/10-11.migrate.products.sh || { echo "ERROR: Failed to migrate product metadata." exit 1 } } fi fi fi
echo "* running the update scripts" if [ -f "/etc/sysconfig/rhn/rhn_register" ] ; then echo " . rhn_register config file" /usr/bin/python -u client_config_update.py /etc/sysconfig/rhn/rhn_register ${CLIENT_OVERRIDES} fi if [ -f "/etc/sysconfig/rhn/up2date" ] ; then echo " . up2date config file" /usr/bin/python -u client_config_update.py /etc/sysconfig/rhn/up2date ${CLIENT_OVERRIDES} fi
echo echo "REGISTRATION" echo "------------" if [ -z "$ACTIVATION_KEYS" ] ; then echo "*** ERROR: in order to bootstrap SUSE Manager Server clients, an activation key or keys" echo " must be created in the SUSE Manager Server web user interface, and the" echo " corresponding key or keys string (XKEY,YKEY,...) must be mapped to" echo " the ACTIVATION_KEYS variable of this script." exit 1 fi
if [ $REGISTER_THIS_BOX -eq 1 ] ; then echo "* registering" files="" directories="" if [ $ALLOW_CONFIG_ACTIONS -eq 1 ] ; then for i in "/etc/sysconfig/rhn/allowed-actions /etc/sysconfig/rhn/allowed-actions/configfiles"; do [ -d "$i" ] || (mkdir -p $i && directories="$i $directories") done [ -f /etc/sysconfig/rhn/allowed-actions/configfiles/all ] || files="$files /etc/sysconfig/rhn/allowed-actions/configfiles/all" [ -n "$files" ] && touch $files fi if [ -z "$PROFILENAME" ] ; then profilename_opt="" else profilename_opt="--profilename=$PROFILENAME" fi /usr/sbin/rhnreg_ks --force --activationkey "$ACTIVATION_KEYS" $profilename_opt RET="$?" [ -n "$files" ] && rm -f $files [ -n "$directories" ] && rmdir $directories if [ $RET -eq 0 ]; then echo echo "*** this system should now be registered, please verify ***" echo else echo echo "*** Error: Registering the system failed." echo exit 1 fi else echo "* explicitly not registering" fi
if [ $ALLOW_CONFIG_ACTIONS -eq 1 ] ; then echo echo "* setting permissions to allow configuration management" echo " NOTE: use an activation key to subscribe to the tools" if [ "$INSTALLER" == zypper ] ; then echo " channel and zypper install/update rhncfg-actions" elif [ "$INSTALLER" == yum ] ; then echo " channel and yum upgrade rhncfg-actions" else echo " channel and up2date rhncfg-actions" fi if [ -x "/usr/bin/rhn-actions-control" ] ; then rhn-actions-control --enable-all rhn-actions-control --disable-run else echo "Error setting permissions for configuration management." echo " Please ensure that the activation key subscribes the" if [ "$INSTALLER" == zypper ] ; then echo " system to the tools channel and zypper install/update rhncfg-actions." elif [ "$INSTALLER" == yum ] ; then echo " system to the tools channel and yum updates rhncfg-actions." else echo " system to the tools channel and up2dates rhncfg-actions." fi exit fi fi
if [ $ALLOW_REMOTE_COMMANDS -eq 1 ] ; then echo echo "* setting permissions to allow remote commands" echo " NOTE: use an activation key to subscribe to the tools" if [ "$INSTALLER" == zypper ] ; then echo " channel and zypper update rhncfg-actions" elif [ "$INSTALLER" == yum ] ; then echo " channel and yum upgrade rhncfg-actions" else echo " channel and up2date rhncfg-actions" fi if [ -x "/usr/bin/rhn-actions-control" ] ; then rhn-actions-control --enable-run else echo "Error setting permissions for remote commands." echo " Please ensure that the activation key subscribes the" if [ "$INSTALLER" == zypper ] ; then echo " system to the tools channel and zypper updates rhncfg-actions." elif [ "$INSTALLER" == yum ] ; then echo " system to the tools channel and yum updates rhncfg-actions." else echo " system to the tools channel and up2dates rhncfg-actions." fi exit fi fi
echo echo "OTHER ACTIONS" echo "------------------------------------------------------" if [ $DISABLE_YAST_AUTOMATIC_ONLINE_UPDATE -eq 1 ]; then YAOU_SYSCFGFILE="/etc/sysconfig/automatic_online_update" if [ -f "$YAOU_SYSCFGFILE" ]; then echo "* Disable YAST automatic online update." sed -i 's/^ *AOU_ENABLE_CRONJOB.*/AOU_ENABLE_CRONJOB="false"/' "$YAOU_SYSCFGFILE" for D in /etc/cron.*; do test -L $D/opensuse.org-online_update && rm $D/opensuse.org-online_update done fi fi if [ "$INSTALLER" == zypper ] ; then test -d /var/lib/suseRegister && touch /var/lib/suseRegister/neverRegisterOnBoot fi if [ $DISABLE_LOCAL_REPOS -eq 1 ]; then if [ "$INSTALLER" == zypper ] ; then echo "* Disable all repos not provided by SUSE Manager Server." zypper ms -d --all zypper ms -e --medium-type plugin zypper mr -d --all zypper mr -e --medium-type plugin zypper mr -e "$Z_CLIENT_REPO_NAME" elif [ "$INSTALLER" == yum ] ; then echo "* Disable all repos not provided by SUSE Manager Server."; for F in /etc/yum.repos.d/*.repo; do test -f "$F" || continue awk ' BEGIN { saw=0 } /^ *[[]/ { if ( saw==1 ) print "enabled=0"; else saw=1 } /^ *enabled *=/ { print "enabled=0"; saw=2; next } { print } END { if ( saw==1 ) print "enabled=0" } ' "$F" > "$F.bootstrap.tmp" && mv "$F.bootstrap.tmp" "$F" test -f "$F.bootstrap.tmp" && { echo "*** Error: Failed to process '$F'; check manually if all repos inside are disabled." rm "$F.bootstrap.tmp" } done fi fi if [ $FULLY_UPDATE_THIS_BOX -eq 1 ] ; then if [ "$INSTALLER" == zypper ] ; then echo "zypper --non-interactive up zypper zypp-plugin-spacewalk; rhn-profile-sync; zypper --non-interactive up (conditional)" elif [ "$INSTALLER" == yum ] ; then echo "yum -y upgrade yum yum-rhn-plugin; rhn-profile-sync; yum upgrade (conditional)" else echo "up2date up2date; up2date -p; up2date -uf (conditional)" fi else if [ "$INSTALLER" == zypper ] ; then echo "zypper --non-interactive up zypper zypp-plugin-spacewalk; rhn-profile-sync" elif [ "$INSTALLER" == yum ] ; then echo "yum -y upgrade yum yum-rhn-plugin; rhn-profile-sync" else echo "up2date up2date; up2date -p" fi fi echo "but any post configuration action can be added here. " echo "------------------------------------------------------" if [ $FULLY_UPDATE_THIS_BOX -eq 1 ] ; then echo "* completely updating the box" else echo "* ensuring $INSTALLER itself is updated" fi if [ "$INSTALLER" == zypper ] ; then zypper lr -u zypper --non-interactive ref -s zypper --non-interactive up zypper zypp-plugin-spacewalk if [ -x /usr/sbin/rhn-profile-sync ] ; then /usr/sbin/rhn-profile-sync else echo "Error updating system info in SUSE Manager Server." echo " Please ensure that rhn-profile-sync in installed and rerun it." fi if [ $FULLY_UPDATE_THIS_BOX -eq 1 ] ; then zypper --non-interactive up fi elif [ "$INSTALLER" == yum ] ; then yum repolist /usr/bin/yum -y upgrade yum yum-rhn-plugin if [ -x /usr/sbin/rhn-profile-sync ] ; then /usr/sbin/rhn-profile-sync else echo "Error updating system info in SUSE Manager Server." echo " Please ensure that rhn-profile-sync in installed and rerun it." fi if [ $FULLY_UPDATE_THIS_BOX -eq 1 ] ; then /usr/bin/yum -y upgrade fi else /usr/sbin/up2date up2date /usr/sbin/up2date -p if [ $FULLY_UPDATE_THIS_BOX -eq 1 ] ; then /usr/sbin/up2date -uf fi fi echo "-bootstrap complete"

# vi /etc/dhcpd.conf
option domain-name "[Domain name]";
option domain-name-servers [DNS IP];
option routers [Gateway ip];
default-lease-time 60;
ddns-update-style none;
host [netboot client hostname] {
  hardware ethernet [MAC address lower case];
  fixed-address [client ip];
  next-server [suse manager ip];
  allow bootp;
  server-name "[susemanager FQDN]";
  filename "boot/grub2/powerpc-ieee1275/core.elf";
}
subnet [Network] netmask [Subnet] {
  range dynamic-bootp [range lowest ip] [range highest ip];
  default-lease-time 5;
  max-lease-time 3600;
  filename "boot/grub2/powerpc-ieee1275/core.elf";
  next-server [suse manager server];
  }

# vi /root/grub2/grub.cfg
with_gfx=0

gfxmode=auto
locale_dir=$prefix/locale
lang=en_US

set default='dev-sle12-sp2-sap-pool-ppc64le:1:IRIS'

insmod gettext

if sleep --interruptible 0 ; then
  timeout=30
fi

menuentry 'dev-sle12-sp2-sap-pool-ppc64le:1:IRIS' --class opensuse --class gnu-linux --class gnu --class os {
insmod http
insmod tftp
set root=tftp,[suma ip]
echo 'Loading kernel ...'
linux /images/dev-sle12-sp2-sap-pool-ppc64le_1_IRIS/linux ksdevice=bootif lang=en_US pt.options=+self_update repo=http://[suma ip]/ks/dist/dev-sle12-sp2-sap-pool-ppc64le install=http://[suma ip]/ks/dist/dev-sle12-sp2-sap-pool-ppc64le self_update=0 textmode=1 autoyast=http://[suma ip]/cblr/svc/op/ks/profile/dev-sle12-sp2-sap-pool-ppc64le:1:IRIS
echo 'Loading initial ramdisk ...'
initrd /images/dev-sle12-sp2-sap-pool-ppc64le_1_IRIS/initrd
}

menuentry 'local' {
  exit
}

submenu 'Other options...' {
 menuentry 'Reboot' {
 reboot
 }

 menuentry 'Exit to Open Firmware' {
 exit
 }
}
